#' Save a ggplot2 plot to file and update file EXIF metadata
#'
#' @description
#' Save a plot or map then update the EXIF metadata for the title, author, and
#' create data. [ggsave_ext()] also supports creating a file name based on a
#' sentence case name with spaces (e.g. "Baltimore city map") and appending a
#' label (e.g. "baltcity") as a prefix to the output file name.
#'
#' [map_ggsave_ext()] can take a list of {ggplot2} plots (e.g. a list of plot
#' generated by [purrr::map()]) and create multiple files using the same
#' parameters or use the {gridExtras} package to create a single combined PDF
#' file.
#'
#' @param plot Plot to save, defaults to last plot displayed. If plot is an
#'   "magick-image" class object, it is converted to a plot using
#'   [magick::image_ggplot()]
#' @inheritParams ggplot2::ggsave
#' @param name Plot name, used to create filename (if filename is `NULL`) using
#'   [filenamr::make_filename()]
#' @inheritParams filenamr::make_filename
#' @param paper Paper matching name from `paper_sizes` (e.g. "letter"). Not case
#'   sensitive.
#' @param orientation Page orientation ("portrait", "landscape", or "square").
#' @param asp Numeric aspect ratio used to determine width or height if only one
#'   of the two arguments is provided; defaults to `NULL`.
#' @param bgcolor Background color to optionally override `plot.background`
#'   theme element.
#' @param exif If `TRUE`, the EXIF metadata for the exported file is updated
#'   with the exifr package; defaults to `FALSE`.
#' @param overwrite If `TRUE` (default), overwrite any existing file with the
#'   same name or ask to overwrite if `ask = TRUE`. Passed to
#'   [filenamr::check_file_overwrite()].
#' @param ask If `TRUE`, ask before overwriting file with the same name.
#'   Defaults to `FALSE`. Passed to [filenamr::check_file_overwrite()].
#' @inheritParams sfext::write_exif
#' @param preview If `TRUE`, open saved file in default system application.
#'   Based on [ggpreview()] from tjmisc package.
#' @inheritDotParams ggplot2::ggsave -width -height -units -bg
#' @example examples/ggsave_ext.R
#' @seealso
#'  [ggplot2::ggsave()]
#' @rdname ggsave_ext
#' @export
#' @importFrom cliExtras set_cli_quiet
#' @importFrom filenamr str_extract_fileext str_remove_fileext make_filename
#'   check_file_overwrite write_exif
#' @importFrom cli cli_alert_success
#' @importFrom ggplot2 ggsave
ggsave_ext <- function(plot = last_plot(),
                       name = NULL,
                       label = NULL,
                       prefix = NULL,
                       postfix = NULL,
                       filename = NULL,
                       device = NULL,
                       filetype = NULL,
                       path = NULL,
                       paper = NULL,
                       orientation = NULL,
                       width = NULL,
                       height = NULL,
                       asp = NULL,
                       units = "in",
                       scale = 1,
                       dpi = 300,
                       bgcolor = NULL,
                       exif = FALSE,
                       title = NULL,
                       author = NULL,
                       keywords = NULL,
                       args = NULL,
                       overwrite = TRUE,
                       ask = FALSE,
                       preview = FALSE,
                       limitsize = TRUE,
                       quiet = FALSE,
                       ...) {
  if (quiet) {
    existing_handler <- getOption("cli.default_handler")
    cliExtras::set_cli_quiet(TRUE)
    on.exit(options("cli.default_handler" = existing_handler))
  }

  dims <-
    set_ggsave_dims(
      paper = paper,
      orientation = orientation,
      width = width,
      height = height,
      asp = asp,
      units = units,
      limitsize = limitsize
    )

  if (is.null(device) && (!is.null(filetype) | !is.null(filename))) {
    filetype <- filetype %||% filenamr::str_extract_fileext(filename)

    if (!is.null(filename)) {
      filename <- filenamr::str_remove_fileext(filename, filetype)
    }

    device <- filetype
  }

  filename <-
    filenamr::make_filename(
      name = name,
      label = label,
      filename = filename,
      fileext = filetype,
      path = path,
      prefix = prefix,
      postfix = postfix
    )

  filenamr::check_file_overwrite(
    filename = filename,
    overwrite = overwrite,
    ask = ask
  )

  if (inherits(plot, "magick-image")) {
    is_pkg_installed("magick")
    cli::cli_alert_success(
      "Converting {.arg plot} from {.cls magick-image} to {.cls ggplot}."
    )
    plot <- magick::image_ggplot(plot)
  }

  ggplot2::ggsave(
    filename = filename,
    plot = plot,
    device = device,
    scale = scale,
    width = dims$width,
    height = dims$height,
    units = dims$units,
    dpi = dpi,
    bg = bgcolor,
    ...
  )

  cli::cli_alert_success(
    c("v" = "Saving {.path {filename}}.")
  )

  if (exif) {
    filenamr::write_exif(
      path = filename,
      fileext = filetype,
      title = title,
      author = author,
      keywords = keywords,
      date = NULL,
      args = args
    )
  }

  if (preview) {
    system2("open", filename)
  }
}

#' Set the dimensions of a plot for ggsave_ext()
#'
#' @noRd
#' @importFrom papersize get_page_size make_page_size
#' @importFrom cli cli_alert_warning
set_ggsave_dims <- function(paper = NULL,
                            orientation = NULL,
                            width = NULL,
                            height = NULL,
                            asp = NULL,
                            units = "in",
                            limitsize = TRUE) {
  if (!is.null(paper)) {
    paper <- papersize::get_page_size(paper, orientation = orientation)

    dims <-
      list(
        "width" = paper[["width"]],
        "height" = paper[["height"]],
        "units" = paper[["units"]]
      )

    return(dims)
  }

  page <-
    papersize::make_page_size(
      width = width,
      height = height,
      asp = asp,
      units = units
    )

  dims <-
    list(
      "width" = page$width,
      "height" = page$height,
      "units" = page$units
    )

  if (any((c(dims$width, dims$height) > 50)) && (units == "in") && limitsize) {
    cli::cli_alert_warning(
      "Switching {.arg units} from default {.val in} to {.val px} when
      dimensions exceed 50 inches and {.code limitsize = TRUE}."
    )

    dims$units <- "px"
  }

  dims
}

#' @rdname ggsave_ext
#' @name ggsave_social
#' @inheritParams sfext::get_social_image
#' @export
#' @importFrom papersize get_social_size
#' @importFrom rlang exec
ggsave_social <- function(plot = last_plot(),
                          image = "Instagram post",
                          platform = NULL,
                          format = NULL,
                          orientation = NULL,
                          name = NULL,
                          filename = NULL,
                          filetype = "jpeg",
                          dpi = 72,
                          width = 1080,
                          height = 1080,
                          units = "px",
                          ...) {
  image_size <-
    papersize::get_social_size(
      name = image,
      platform = platform,
      format = format,
      orientation = orientation
    )

  params <-
    modify_fn_fmls(
      params = list2(...),
      fn = ggsave_ext,
      plot = plot,
      width = image_size$width,
      height = image_size$height,
      name = name,
      filename = filename,
      filetype = filetype,
      units = units
    )

  rlang::exec(
    ggsave_ext,
    !!!params
  )
}

#' @name map_ggsave_ext
#' @rdname ggsave_ext
#' @param single_file If `TRUE`, use [gridExtra::arrangeGrob()] to create an
#'   arrangelist class object that [ggplot2::ggsave()] can save as a single
#'   multi-page file. Note: this does not work with plots modified with
#'   patchwork including inset maps created with the
#'   [layer_inset()] function.
#' @export
#' @importFrom filenamr make_filename check_file_overwrite
#' @importFrom purrr map
#' @importFrom cli cli_bullets
map_ggsave_ext <- function(plot,
                           name = NULL,
                           label = NULL,
                           prefix = NULL,
                           postfix = "pg_",
                           filename = NULL,
                           device = NULL,
                           filetype = NULL,
                           path = NULL,
                           overwrite = TRUE,
                           ...,
                           single_file = TRUE) {
  if (!is.list(plot)) {
    cli_abort(
      "{.arg plot} must be a {.cls list}."
    )
  }

  filename <-
    filenamr::make_filename(
      name = name,
      label = label,
      filename = filename,
      fileext = filetype,
      path = NULL,
      prefix = prefix
    )

  is_patchwork_plot <- is_patchwork(plot)

  if (single_file & !is_patchwork_plot) {
    is_pkg_installed("gridExtra")

    plot <-
      purrr::map(
        seq(plot),
        ~ gridExtra::arrangeGrob(plot[[.x]])
      )

    class(plot) <- c("arrangelist", class(plot))

    ggsave_ext(
      plot = plot,
      filename = filename,
      path = path,
      device = device,
      ...
    )

    return(invisible())
  }

  if (single_file) {
    if (has_fileext(filename, "pdf")) {
      output_path <- path
      path <- tempdir()
    } else {
      cli::cli_bullets(
        c(
          "!" = "{.arg single_file} = TRUE only works with {.cls patchwork}
          plots when saving a pdf file.",
          "i" = "Setting {.arg single_file} to {.code FALSE}."
        )
      )
      single_file <- FALSE
    }
  }

  for (pg in seq_along(plot)) {
    pg_postfix <- glue("{postfix}{pg}")

    ggsave_ext(
      plot = plot[[pg]],
      postfix = pg_postfix,
      filename = filename,
      path = path,
      device = device,
      ...
    )
  }

  if (!single_file) {
    return(invisible())
  }

  is_pkg_installed("qpdf")

  input <-
    list.files(
      path = path,
      pattern = "*.pdf"
    )

  filenamr::check_file_overwrite(
    filename = filename,
    path = output_path,
    overwrite = overwrite,
    ask = FALSE
  )

  if (!is.null(output_path)) {
    output <- file.path(output_path, filename)
  }

  qpdf::pdf_combine(
    input = input,
    output = output
  )

  unlink(path, recursive = TRUE)
}
